function getRandom(arr,n){var result=new Array(n),len=arr.length,taken=new Array(len);if(n>len)throw new RangeError("getRandom: more elements taken than available");for(;n--;){var x=Math.floor(Math.random()*len);result[n]=arr[x in taken?taken[x]:x],taken[x]=--len in taken?taken[len]:len}return result}String.prototype.turkishToUpper=function(){var string=this,letters={i:"İ","ş":"Ş","ğ":"Ğ","ü":"Ü","ö":"Ö","ç":"Ç","ı":"I"};return(string=string.replace(/(([iışğüçö]))/g,(function(letter){return letters[letter]}))).toUpperCase()};