var correctAnswer="";function get_related_words(){window.answerWord.value="";var response=dbWords[Math.floor(Math.random()*dbWords.length)];correctAnswer=response.name,window.wordLength.innerHTML=correctAnswer.length,window.firstLetter.innerHTML=correctAnswer.charAt(0);var relatedWords=response.relations,relatedWordsString="";0==relatedWords.length&&get_related_words();for(var randCount=relatedWords.length>4?5:relatedWords.length,randomItems=getRandom(relatedWords,randCount),i=0;i<randomItems.length;i++)relatedWordsString+='<span class="relWorkItem">'+randomItems[i]+"</span>",i===parseInt(randomItems.length/2)-1&&(relatedWordsString+="<br><br>");document.getElementById("relatedwords").innerHTML=relatedWordsString}function send_answer(){var answer=window.answerWord.value.turkishToUpper();""===answer||" "===answer?toastr.info("Bir kelime yazmalısınız!"):correctAnswer===answer?(toastr.success("Tebrikler! Doğru cevap!"),get_related_words()):toastr.error("Yanlış cevap!")}get_related_words(),window.answerWord.addEventListener("keyup",(function(event){13===event.keyCode&&(event.preventDefault(),send_answer())})),window.answerWord=document.getElementById("answerWord"),window.wordLength=document.getElementById("wordLength"),window.firstLetter=document.getElementById("firstLetter");